# Function to upload image
upload_image() {
    local file_path=$1
    local filename=$(basename "$file_path")
    
    echo "Uploading $filename..."
    
    UPLOAD_RESPONSE=$(curl -s -X POST \
        -H "Authorization: Basic ${BASE64_TOKEN}" \
        -F "file=@$file_path" \
        "${SITE_URL}/wp-json/wp/v2/media")
    
    # Extract media ID
    MEDIA_ID=$(echo "$UPLOAD_RESPONSE" | grep -o '"id":[0-9]*' | head -1 | cut -d':' -f2)
    # Extract media URL
    MEDIA_URL=$(echo "$UPLOAD_RESPONSE" | grep -o '"source_url":"[^"]*' | cut -d'"' -f4)
    
    if [ -n "$MEDIA_ID" ] && [ -n "$MEDIA_URL" ]; then
        echo "✅ $filename uploaded successfully (ID: $MEDIA_ID)"
        echo "URL: $MEDIA_URL"
        echo ""
        return 0
    else
        echo "❌ Failed to upload $filename"
        echo "Response: $UPLOAD_RESPONSE"
        echo ""
        return 1
    fi
}

