# Create WordPress page
POST_RESPONSE=$(curl -s -X POST \
  -H "Authorization: Basic ${BASE64_TOKEN}" \
  -H "Content-Type: application/json" \
  -d "$POST_DATA" \
  "${SITE_URL}/wp-json/wp/v2/pages")

echo "Post creation response: $POST_RESPONSE"

# Extract post URL from response
POST_URL=$(echo "$POST_RESPONSE" | grep -o '"link":"[^"]*' | cut -d'"' -f4)

if [ -n "$POST_URL" ]; then
    echo ""
    echo "âœ… Tea Bar page created successfully on chaba-ba.jpn.org!"
    echo "View your page at: $POST_URL"
else
    echo ""
    echo "âŒ Failed to create Tea Bar page"
fi