# Update WordPress homepage
UPDATE_RESPONSE=$(curl -s -X POST \
  -H "Authorization: Basic ${BASE64_TOKEN}" \
  -H "Content-Type: application/json" \
  -d "$POST_DATA" \
  "${SITE_URL}/wp-json/wp/v2/pages/${HOMEPAGE_ID}")

echo "Update response: $UPDATE_RESPONSE"

# Extract post URL from response
POST_URL=$(echo "$UPDATE_RESPONSE" | grep -o '"link":"[^"]*' | cut -d'"' -f4)

if [ -n "$POST_URL" ]; then
    echo ""
    echo "âœ… Homepage updated successfully with Kadence-style design!"
    echo "View your homepage at: $POST_URL"
else
    echo ""
    echo "âŒ Failed to update homepage"
fi